import React, { useState, useEffect, FC, useMemo, useCallback } from "react";
import { View, StyleSheet, Image, Platform } from "react-native";
import { Text, useTheme, TextInput, Button, RadioButton, Checkbox } from "react-native-paper";

import flying_routes from "../../assets/files/flying_routes.json"
import fixed_routes from "../../assets/files/fixed_routes.json"
import routes from "../../assets/files/routes.json"


interface Props {
    routes:any,
    setRoutes:any,
}

export const SortRoutes: FC<Props> = (props) => {
    // transportation types:
    // 1 - plane
    // 2 - train (I guess) 
    // 3 - car (I guess)

    const [plane, setPlane] = useState(false)
    const [train, setTrain] = useState(false)
    const [car, setCar] = useState(false)

    const checkBoxPressHandler = (value:boolean,setValue:any) => {
        setBox(value)

    }

    return (
        <View>
            <Text>SortRoutes</Text>
                <View style={styles.checkBoxContainer}>
                    <View style={styles.checkBox}>
                        <Text>Plane</Text>
                        <Checkbox status={plane?"checked":"unchecked"} onPress={()=>checkBoxPressHandler(!plane,setPlane)} />
                    </View>
                    <View style={styles.checkBox}>
                        <Text>Train</Text>
                        <Checkbox status={train?"checked":"unchecked"} onPress={()=>checkBoxPressHandler(!train,setTrain)} />
                    </View>
                    <View style={styles.checkBox}>
                        <Text>Car</Text>
                        <Checkbox status={car?"checked":"unchecked"} onPress={()=>checkBoxPressHandlersetCar(!car)} />
                    </View>
                </View>
            {/* <Text>{JSON.stringify(flying_routes)}</Text> */}

        </View>)
}

const styles = StyleSheet.create({


    checkBoxContainer: {
        // flex:1,
        display: "flex",
        flexDirection: "row",
        justifyContent: "flex-start",
        alignItems: "center",
        width: "100%",
    },
    checkBox: {
        display: "flex",
        flexDirection: "row",
        justifyContent: "flex-start",
        alignItems: "center",
    }


})
