import { TouchableOpacity, StyleSheet, Text, View, Dimensions } from "react-native";
import React, { useContext } from "react";
import { generateColors } from "../../scheme/colors";
import { Context } from "../../../App";
import { Location } from "../../screens/Home";
const colors = generateColors.colors;

const screenHeight = Dimensions.get("window").height;

interface TeavelData {
  id: number,
  from: number,
  to: number,
  transportation_type: number,
  euro_price: number,
  time_in_minutes: number,
}

interface Props {
  bottom: boolean,
  travelDataId:number,
}

const ListBottomComponent: React.FC<Props> = ({ bottom }) => {

  const buyTicketHandler = () => {
    console.log("Buy ticket");
  }

  const contextData: any = useContext(Context);

  const getTravelDataById = (id: number) => contextData.travelData.find((data: TeavelData) => data.id == id)
  const getLocationById = (id: number) => contextData.locations.find((loc: Location) => loc.id == id)

  return (
    <View
      style={[
        styles.itemContainer,
        {
          borderBottomLeftRadius: bottom ? 5 : 0,
          borderBottomRightRadius: bottom ? 5 : 0,
        },
      ]}
    >

      <View style={styles.directions}>
        <Text style={styles.boldText}>Kiev -> Wroclaw</Text>
        <Text>Flight</Text>
      </View>
      <View style={[styles.directions, { marginTop: 10 }]}>
        <Text>3 h 55m</Text>
        <TouchableOpacity onPress={buyTicketHandler}>
          <Text style={styles.buyTicket}>Buy Ticket</Text>
        </TouchableOpacity>
        <Text style={styles.price}>9.9 â‚¬ </Text>
      </View>
    </View>

  );
};

export default ListBottomComponent;

const styles = StyleSheet.create({
  itemContainer: {
    height: screenHeight / 10,
    width: "100%",
    backgroundColor: colors.surface,
    borderTopWidth: 0,
    borderWidth: 1,
    borderColor: "grey",
    alignItems: "center",
    justifyContent: "center",
  },
  dropped: {
    flex: 1,
    margin: 10,
  },
  directions: {
    flexDirection: "row",
    justifyContent: "space-between",
    width: "100%",
    padding: 5,
  },
  boldText: {
    fontWeight: "bold",
  },
  buyTicket: {
    borderWidth: 1,
    borderColor: colors.primary,
  },
  price: {
    color: colors.primary,
  }
});
