import React, { useState, useEffect, FC, useMemo, useCallback } from "react";
import { View, StyleSheet, Image, Platform } from "react-native";
import { Text, useTheme, TextInput, Button, RadioButton, Checkbox} from "react-native-paper";

export const SortRoutes: FC = () => {
    // transportation types:
    // 1 - plane
    // 2 - train (I guess) 
    // 3 - car (I guess)

    const [transportationType, setTransportationType] = useState("2")

    return (
        <View>
            <Text>SortRoutes</Text>
            <RadioButton.Group onValueChange={newValue => setTransportationType(newValue)} value={transportationType}>
                <View style={styles.radioButtonContainer}>
                    <View>
                        <Text>Plane</Text>
                        <RadioButton value={"1"} />
                    </View>
                    <View>
                        <Text>Train</Text>
                        <RadioButton value={"2"} />
                    </View>
                    <View>
                        <Text>Car</Text>
                        <RadioButton value={"3"} />
                    </View>
                </View>
            </RadioButton.Group>
            <Checkbox status=""></Checkbox>


        </View>)
}

const styles = StyleSheet.create({


    radioButtonContainer: {
        // flex:1,
        display: "flex",
        flexDirection: "row",
        justifyContent: "flex-start",
        alignItems: "center",
        width: "100%",
    },


})
