import React, { useState, useEffect, FC, useMemo, useCallback } from "react";
import { View, StyleSheet, Image, Platform } from "react-native";
import { Text, useTheme, TextInput, Button, RadioButton, Checkbox } from "react-native-paper";

export const SortRoutes: FC = () => {
    // transportation types:
    // 1 - plane
    // 2 - train (I guess) 
    // 3 - car (I guess)

    const [transportationType, setTransportationType] = useState("2")
    const [plane, setPlane] = useState(false)
    const [train, setTrain] = useState(false)
    const [car, setCar] = useState(false)

    return (
        <View>
            <Text>SortRoutes</Text>
                <View style={styles.radioButtonContainer}>
                    <View s>
                        <Text>Plane</Text>
                        <Checkbox status={plane?"checked":"unchecked"} onPress={()=>setPlane(!plane)} />
                    </View>
                    <View>
                        <Text>Train</Text>
                        <Checkbox status={train?"checked":"unchecked"} onPress={()=>setTrain(!train)} />
                    </View>
                    <View>
                        <Text>Car</Text>
                        <Checkbox status={car?"checked":"unchecked"} onPress={()=>setCar(!car)} />
                    </View>
                </View>


        </View>)
}

const styles = StyleSheet.create({


    radioButtonContainer: {
        // flex:1,
        display: "flex",
        flexDirection: "row",
        justifyContent: "flex-start",
        alignItems: "center",
        width: "100%",
    },


})
